document.addEventListener('DOMContentLoaded', async function() {
	document.getElementById('signupSign').style.display = "none";
	document.getElementById('loginSign').style.display = "none";

	document.getElementById('signupBtn').addEventListener('click', async function() {
		await signup(document.getElementById("signupUsername").value, document.getElementById("signupPassword").value, "", "data:image/png;base64,");
	});

	document.getElementById('loginBtn').addEventListener('click', async function() {
		await login(document.getElementById("loginUsername").value, document.getElementById("loginPassword").value);
	});

	document.getElementById('profSign').addEventListener('click', function() {
		var username = document.getElementById('dropdownUsername').innerHTML;
		username = username.substr(0, username.indexOf('<span') - 1);
		localStorage.setItem("currentProfile", username);
	});

	document.getElementById('logoutSign').addEventListener('click', async function() {
		await logout(localStorage.getItem("token"));
	});

	document.getElementById('homepage').addEventListener('click', function() {
		location.href = "../html/homepage.html";
	});

	document.getElementById('about').addEventListener('click', function() {
		location.href = "../html/about.html";
	});

	document.getElementById('allposts').addEventListener('click', function() {
		localStorage.setItem("currentSearch", "null");
		location.href = "../html/allposts.html";
	});
	
	document.getElementById("logout").addEventListener('click', function() {
		location.href = "../html/homepage.html";
	})



	$('#loginmodal').on('hidden.bs.modal', function() {
		document.getElementById("loginPassword").value = "";
    	document.getElementById("loginUsername").value = "";
	});

	$('#loginmodal').on('show.bs.modal', function() {
		document.getElementById("signupincorrect").style.display = "none";
		document.getElementById("loginincorrect").style.display = "none";
	});

	$('#signupmodal').on('hidden.bs.modal', function() {
		document.getElementById("signupPassword").value = "";
    	document.getElementById("signupUsername").value = "";
	});

	$('#signupmodal').on('show.bs.modal', function() {
		document.getElementById("signupincorrect").style.display = "none";
		document.getElementById("loginincorrect").style.display = "none";
	});

	await reload();
});

async function reload() {
	if (localStorage.getItem("token") !== "null" && localStorage.getItem("token") !== null) {
		var info = await userInfoToken(localStorage.getItem("token"));
		document.getElementById('dropdown').style.display = "flex";
		document.getElementById('signupSign').style.display = "none";
		document.getElementById('loginSign').style.display = "none";
		document.getElementById('dropdownUsername').innerHTML = info[0] + " <span class='caret'></span>";
	} else {
		document.getElementById('dropdown').style.display = "none";
		document.getElementById('signupSign').style.display = "block";
		document.getElementById('loginSign').style.display = "block";
	}
}


async function authUser(token) {
	var testURL = createURL("authUser", { 
		"token": token
	});
	var res = await fetch(testURL);
	var name = await res.text();
	console.log(name);
	
	return name;
}



async function userInfoToken(token) {
	var testURL = createURL("authUser", { 
		"token": token
	});
	var res = await fetch(testURL);
	var name = await res.text();
	console.log(name);
	
	return await userInfoName(name);
}

async function userInfoName(name) {
	testURL = createURL("userInfo", { 
		"name": name
	});

	var user = await fetch(testURL);
	user = await user.text();
	console.log(user);
	return JSON.parse(user);
}

async function logout(token) {
	var testURL = createURL("logout", { 
		"token": token
	});
	
	await fetch(testURL);
	localStorage.setItem("token", null);
	await reload();

}


async function signup(name, pwd, description, image) {
	let data = {
		"name": name, 
		"pwd": pwd,
		"description": description,
		"image": image
	}

	console.log(JSON.stringify(data));
	await fetch("http://localhost:3000/addUser", {
		headers: {
			'Accept': 'application/json',
			'Content-Type': 'application/json'
		},
		method: "POST", 
		body: JSON.stringify(data)
	})
	
	var testURL = createURL("login", {
		"name": data.name,
		"pwd": data.pwd
	});
	
	var result = await fetch(testURL);
	var text = await result.text();
	localStorage.setItem("token", text);

	await reload();
	$('#signupmodal').modal('hide');
}

async function login(name, pwd) {
	var testURL = createURL("login", {
		"name": name,
		"pwd": pwd
	});
	
	var result = await fetch(testURL);
	var text = await result.text();
	if (text !== "") {
		localStorage.setItem("token", text);
		await reload();
		$('#loginmodal').modal('hide');
	} else {
		document.getElementById("loginincorrect").style.display = "block";
	}
}

function createURL(endpoint, params) {
	var url = "http://localhost:3000/";
	url += endpoint;
	if (Object.keys(params).length > 0) {
		url += "?";
		for (var key in params) {
			url += key;
			url += "=";
			url += params[key];
			url += "&";

		}
	}
	return url;
}